import sqlite3# Функция для сохранения объявления в базу данныхdef save_ad_to_db(title, price, url):    conn = sqlite3.connect('database.db')    cursor = conn.cursor()    # Создаем таблицу, если она еще не существует    cursor.execute('''        CREATE TABLE IF NOT EXISTS ads (            id INTEGER PRIMARY KEY AUTOINCREMENT,            title TEXT,            price INTEGER,            url TEXT        )    ''')    # Вставляем данные    cursor.execute('''        INSERT INTO ads (title, price, url)        VALUES (?, ?, ?)    ''', (title, price, url))    conn.commit()    conn.close()# Функция для получения объявлений из базы данныхdef get_ads_from_db():    conn = sqlite3.connect('database.db')    cursor = conn.cursor()    cursor.execute('SELECT * FROM ads')    ads = cursor.fetchall()    conn.close()    return ads# Функция для закрытия соединения с базой данных (опционально)def close_connection():    pass